-- 1. Shared wordlists table (no RLS - all users share built-in lists)
CREATE TABLE IF NOT EXISTS wordlists (
  id serial PRIMARY KEY,
  name text NOT NULL,
  source text NOT NULL,
  created_at timestamptz DEFAULT now()
);

-- 2. Shared words table (no RLS - all users share)
CREATE TABLE IF NOT EXISTS words (
  id serial PRIMARY KEY,
  wordlist_id integer REFERENCES wordlists(id) ON DELETE CASCADE NOT NULL,
  word text NOT NULL,
  meaning_cn text,
  unit text,
  phonetic text DEFAULT '',
  example1 text DEFAULT '',
  example2 text DEFAULT '',
  source text DEFAULT ''
);

-- 3. Indexes
CREATE INDEX IF NOT EXISTS idx_words_wordlist ON words(wordlist_id);
CREATE INDEX IF NOT EXISTS idx_words_word ON words(word);
CREATE INDEX IF NOT EXISTS idx_uws_word_text ON user_word_state(word_text);
CREATE INDEX IF NOT EXISTS idx_uws_user_id ON user_word_state(user_id);
CREATE INDEX IF NOT EXISTS idx_sessions_user_id ON sessions(user_id);

